sys_script_include.script:
class ParentGroupAssigner {


    getGroupConfiguration(attachmentSysId) {
        let groupConfigs = new Set();

        var parser = new sn_impex.GlideExcelParser();
        var attachment = new GlideSysAttachment();
        var attachmentStream = attachment.getContentStream(attachmentSysId);
        parser.parse(attachmentStream);

        while (parser.next()) {
            var row = parser.getRow();
            groupConfigs.add(this.prepareData(row));
        }

        return groupConfigs;
    }

    getAttachmentSysId(name) {
        let sysId = '';
        const query = 'file_name=' + name + '^table_name=sys_script_include';
        let record = x_984628_core_sm_n.GlideUtils.getGlideRecord('sys_attachment', query);

        if (record.next()) {
            sysId = record.getValue('sys_id');
        }

        return sysId;
    }


    prepareData(row) {
        let groupConfig = {
            "parent": row['Parent Group'],
            "childe": row['Group']
        };

        return groupConfig;
    }


    assignParentGroup(groupConfigs) {
        for (const groupConfig of groupConfigs) {
            gs.info(JSON.stringify(groupConfig));
        }
    }

}
